mapper.update:
UPDATE
		member 
SET
		member_pw = #{memberPw}
WHERE
		member_id = #{memberId}

mapper.select:

	SELECT 
		member_email as memberEmail
		
	FROM 
		member
	WHERE 
		member_name = #{memberName}
	AND
		member_id = #{memberId}
	AND
		member_email = #{memberEmail}

	
		SELECT
			member_id as memberId,
			member_name as memberName,
			member_pw as memberPw
		FROM
			member
		WHERE
		
			member_name = #{memberName}
		AND
			member_phone = #{memberPhone}
		AND
			member_id = #{memberId}
			
		OR
		
			member_name = #{memberName}
		AND
			member_email = #{memberEmail}
		AND
			member_id = #{memberId}

mapper.update:
UPDATE
		member 
SET
		member_pw = #{memberUpdatePw}
WHERE
		member_id = #{memberId} 
AND
		member_pw = #{memberPw}

mapper.select:
SELECT member_id as memberId, member_pw as memberPw, member_name as memberName, member_phone as memberPhone, member_address as memberAddress, member_gender as memberGender, member_level as memberLevel, member_email as memberEmail
FROM member
WHERE member_id = #{memberId} AND member_pw = #{memberPw}
